def reverse_comp(nucleotide_sequence):
    NUCLEOTIDE_DICT = {'A': 'T', 'C': 'G', 'G': 'C', 'T': 'A'}
    output = [NUCLEOTIDE_DICT[i] for i in nucleotide_sequence]
    output.reverse()
    return ''.join(output)


if __name__ == '__main__':
    # test case
    pattern = "AAAACCCGGT"
    reverse_comp_pattern = reverse_comp(pattern)
    print(reverse_comp_pattern)

    # user case example
    pattern = ""
    reverse_comp_pattern = reverse_comp(pattern)
    print(reverse_comp_pattern)