def hamming_distance(str1, str2):
    return sum(1 for i, j in zip(str1, str2) if i != j)


if __name__ == '__main__':
    # test case
    str1 = "GGGCCGTTGGT"
    str2 = "GGACCGTTGAC"
    hamm_dist = hamming_distance(str1, str2)
    print(hamm_dist)

    str1 = ""
    str2 = ""
    hamm_dist = hamming_distance(str1, str2)
    print(hamm_dist)
